import "./styles.scss";

import { Color } from "@tiptap/extension-color";
import ListItem from "@tiptap/extension-list-item";
import TextStyle from "@tiptap/extension-text-style";
import { EditorProvider, useCurrentEditor } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";
import React, { useEffect, useState, useRef } from "react";

// Floating Menu component that appears when text is selected
const FloatingMenu = () => {
  const { editor } = useCurrentEditor();
  const [isVisible, setIsVisible] = useState(false);
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const menuRef = useRef(null);

  useEffect(() => {
    if (!editor) return;

    const updateMenu = () => {
      const { state } = editor;
      const { selection } = state;
      const { empty } = selection;

      // Hide the menu if there's no selection
      if (empty) {
        setIsVisible(false);
        return;
      }

      // Show the menu and position it above the selection
      const { ranges } = selection;
      const from = Math.min(...ranges.map(range => range.$from.pos));
      const to = Math.max(...ranges.map(range => range.$to.pos));

      // Get the coordinates of the selection
      const view = editor.view;
      const { top, left, height } = view.coordsAtPos(from);
      const { right } = view.coordsAtPos(to);

      // Position the menu above the selection
      setPosition({
        top: top - (menuRef.current?.offsetHeight || 0) - 10,
        left: left + (right - left) / 2
      });

      setIsVisible(true);
    };

    // Update the menu position when the selection changes
    editor.on('selectionUpdate', updateMenu);
    editor.on('focus', updateMenu);
    editor.on('blur', () => setIsVisible(false));

    return () => {
      editor.off('selectionUpdate', updateMenu);
      editor.off('focus', updateMenu);
      editor.off('blur', () => setIsVisible(false));
    };
  }, [editor]);

  if (!editor || !isVisible) {
    return null;
  }

  return (
    <div 
      className="floating-menu" 
      ref={menuRef}
      style={{
        position: 'absolute',
        top: `${position.top}px`,
        left: `${position.left}px`,
        transform: 'translateX(-50%)',
        zIndex: 100,
        display: isVisible ? 'flex' : 'none',
      }}
    >
      <button
        onClick={() => editor.chain().focus().toggleBold().run()}
        className={editor.isActive("bold") ? "is-active" : ""}
        title="Bold"
      >
        B
      </button>
      <button
        onClick={() => editor.chain().focus().toggleItalic().run()}
        className={editor.isActive("italic") ? "is-active" : ""}
        title="Italic"
      >
        I
      </button>
      <button
        onClick={() => editor.chain().focus().toggleStrike().run()}
        className={editor.isActive("strike") ? "is-active" : ""}
        title="Strike"
      >
        S
      </button>
      <button
        onClick={() => editor.chain().focus().toggleCode().run()}
        className={editor.isActive("code") ? "is-active" : ""}
        title="Code"
      >
        <code>{`<>`}</code>
      </button>

      <div className="divider"></div>

      <select 
        onChange={(e) => {
          const value = e.target.value;
          if (value === 'paragraph') {
            editor.chain().focus().setParagraph().run();
          } else if (value.startsWith('h')) {
            const level = parseInt(value.substring(1));
            editor.chain().focus().toggleHeading({ level }).run();
          }
        }}
        value={
          editor.isActive('heading', { level: 1 }) ? 'h1' :
          editor.isActive('heading', { level: 2 }) ? 'h2' :
          editor.isActive('heading', { level: 3 }) ? 'h3' :
          editor.isActive('heading', { level: 4 }) ? 'h4' :
          editor.isActive('heading', { level: 5 }) ? 'h5' :
          editor.isActive('heading', { level: 6 }) ? 'h6' :
          'paragraph'
        }
        title="Text style"
      >
        <option value="paragraph">Paragraph</option>
        <option value="h1">Heading 1</option>
        <option value="h2">Heading 2</option>
        <option value="h3">Heading 3</option>
        <option value="h4">Heading 4</option>
        <option value="h5">Heading 5</option>
        <option value="h6">Heading 6</option>
      </select>

      <div className="divider"></div>

      <button
        onClick={() => editor.chain().focus().toggleBulletList().run()}
        className={editor.isActive("bulletList") ? "is-active" : ""}
        title="Bullet List"
      >
        ‚Ä¢
      </button>
      <button
        onClick={() => editor.chain().focus().toggleOrderedList().run()}
        className={editor.isActive("orderedList") ? "is-active" : ""}
        title="Ordered List"
      >
        1.
      </button>
      <button
        onClick={() => editor.chain().focus().toggleBlockquote().run()}
        className={editor.isActive("blockquote") ? "is-active" : ""}
        title="Blockquote"
      >
        "
      </button>

      <div className="divider"></div>

      <select
        onChange={(e) => {
          const color = e.target.value;
          if (color) {
            editor.chain().focus().setColor(color).run();
          }
        }}
        title="Text color"
      >
        <option value="">Color</option>
        <option value="#958DF1">Purple</option>
        <option value="#F98181">Red</option>
        <option value="#FBBC88">Orange</option>
        <option value="#FAF594">Yellow</option>
        <option value="#70CFF8">Blue</option>
        <option value="#94FADB">Teal</option>
        <option value="#B9F18D">Green</option>
      </select>
    </div>
  );
};

// Traditional MenuBar for reference and as a fallback
const MenuBar = () => {
  const { editor } = useCurrentEditor();

  if (!editor) {
    return null;
  }

  return (
    <div className="menu-bar">
      <button
        onClick={() => editor.chain().focus().undo().run()}
        disabled={!editor.can().chain().focus().undo().run()}
        title="Undo"
      >
        Undo
      </button>
      <button
        onClick={() => editor.chain().focus().redo().run()}
        disabled={!editor.can().chain().focus().redo().run()}
        title="Redo"
      >
        Redo
      </button>
      <button onClick={() => editor.chain().focus().unsetAllMarks().run()} title="Clear formatting">
        Clear formatting
      </button>
    </div>
  );
};

const extensions = [
  Color.configure({ types: [TextStyle.name, ListItem.name] }),
  TextStyle.configure({ types: [ListItem.name] }),
  StarterKit.configure({
    bulletList: {
      keepMarks: true,
      keepAttributes: false, // TODO : Making this as `false` becase marks are not preserved when I try to preserve attrs, awaiting a bit of help
    },
    orderedList: {
      keepMarks: true,
      keepAttributes: false, // TODO : Making this as `false` becase marks are not preserved when I try to preserve attrs, awaiting a bit of help
    },
  }),
];

const content = `
<h2>
  Hi there,
</h2>
<p>
  this is a <em>basic</em> example of <strong>tiptap</strong>. Sure, there are all kind of basic text styles you'd probably expect from a text editor. But wait until you see the lists:
</p>
<ul>
  <li>
    That's a bullet list with one ‚Ä¶
  </li>
  <li>
    ‚Ä¶ or two list items.
  </li>
</ul>
<p>
  Isn't that great? And all of that is editable. But wait, there's more. Let's try a code block:
</p>
<pre><code class="language-css">body {
display: none;
}</code></pre>
<p>
  I know, I know, this is impressive. It's only the tip of the iceberg though. Give it a try and click a little bit around. Don't forget to check the other examples too.
</p>
<blockquote>
  Wow, that's amazing. Good work, boy! üëè
  <br />
  ‚Äî Mom
</blockquote>
`;

export const App = () => {
  return (
    <div className="editor-container">
      <EditorProvider
        slotBefore={<MenuBar />}
        extensions={extensions}
        content={content}
      >
        <FloatingMenu />
      </EditorProvider>
      <div className="editor-instructions">
        <p>Select text to see formatting options</p>
      </div>
    </div>
  );
};

export default App;
